mapper.select:
SELECT u.u_id AS uId, u.u_user AS uUser, IF(u.u_startusing=0,'删除', IF(u.u_startusing=1,'启用','不启用')) AS uStartusing, a.authority_name AS authorityName FROM user AS u LEFT JOIN authority AS a ON a.authority_id = u.u_authority_id
u.u_user LIKE "%"#{uUser}"%"
AND u.u_startusing = #{uStartusing}
LIMIT ${(current-1)*size},${size}

SELECT COUNT(u.u_id) FROM user AS u
u.u_user LIKE "%"#{uUser}"%"
AND u.u_startusing = #{uStartusing}
