mapper.select:
SELECT v.vId, v.vName, IF(v.vEnable = 0, '删除',IF(v.vEnable = 1, '启用','不启用')) vEnable, v.vNote, v.vCreationtime, ( SELECT COUNT(e.eId) FROM entrance AS e WHERE e.evId = v.vId AND e.eEnable = 1 ) AS openEntranceCount, ( SELECT COUNT(e.eId) FROM entrance AS e WHERE e.evId = v.vId AND e.eEnable != 0 ) AS openEntranceCountTotal FROM venues AS v
v.vName LIKE "%"#{vmName}"%"
AND v.vEnable = #{vmEnable}
LIMIT ${(current-1)*size},${size}

SELECT COUNT(v.vId) FROM venues AS v
v.vName LIKE "%"#{vmName}"%"
AND v.vEnable = #{vmEnable}
