mapper.select:
SELECT vm.vm_id AS vmId, vm.vm_name AS vmName, IF(vm.vm_enable = 0, '不启用','启用') AS vmEnable, vm.vm_note AS vmNote, vm.vm_creationtime AS vmCreationtime, u.u_user AS uUser FROM venue_management AS vm LEFT JOIN user AS u ON u.u_id = vm.vm_u_id
vm.vm_name LIKE "%"#{vmName}"%"
AND vm.vm_enable = #{vmEnable}
LIMIT ${(current-1)*size},${size}

SELECT COUNT(vm.vm_id) FROM venue_management AS vm LEFT JOIN user AS u ON u.u_id = vm.vm_u_id
vm.vm_name LIKE "%"#{vmName}"%"
AND vm.vm_enable = #{vmEnable}
